checkitem.compliant:
(
       ( COMPONENT = 'DW4CORE' and VERSION = '100' and not( to_integer( REPLACE_REGEXPR ('\.' IN (COALESCE(NULLIF(SP,''),'0')) with '')) <   12 )   ) 
     )

checkitem.noncompliant:
(
       ( COMPONENT = 'DW4CORE' and VERSION = '100' and to_integer( REPLACE_REGEXPR ('\.' IN (COALESCE(NULLIF(SP,''),'0')) with '')) <   12 )  
     )

(
 ( COMPONENT = 'DW4CORE' and VERSION = '100' and to_integer( REPLACE_REGEXPR ('\.' IN (COALESCE(NULLIF(SP,''),'0')) with '')) between    8 and   11 )  
  )
