checkitem.noncompliant:
(
		  ( COMPONENT = 'ST' and VERSION = '720' and (lpad(SP,4,'0'))  < '0022' )  

		 )

(
		  ( COMPONENT = 'ST' and VERSION = '720' and lpad(SP,4,'0') between '0003' and '0021' )  

		 )

checkitem.compliant:
( ( COMPONENT = 'SRMNXP01' and VERSION = '100' and not( (lpad(SP,4,'0')) < '0033' ) )
or ( COMPONENT = 'SRMNXP01' and VERSION = '150' and not( (lpad(SP,4,'0')) < '0025' ) )
)

checkitem.noncompliant:
( ( COMPONENT = 'SRMNXP01' and VERSION = '100' and (lpad(SP,4,'0')) < '0033' )
or ( COMPONENT = 'SRMNXP01' and VERSION = '150' and (lpad(SP,4,'0')) < '0025' )
)

( ( COMPONENT = 'SRMNXP01' and VERSION = '100' and lpad(SP,4,'0') between '0001' and '0032' )
or ( COMPONENT = 'SRMNXP01' and VERSION = '150' and lpad(SP,4,'0') between '0001' and '0024' )
)
